import React, { useEffect, useState } from 'react';
import { fetchEmployees, createEmployee, updateEmployee, deleteEmployee } from './api';
import EmployeeList from './components/EmployeeList';
import EmployeeForm from './components/EmployeeForm';


export default function App() {
const [employees, setEmployees] = useState([]);
const [editing, setEditing] = useState(null);


useEffect(() => { load(); }, []);


async function load() {
const data = await fetchEmployees();
setEmployees(data);
}


async function handleCreate(emp) {
const res = await createEmployee(emp);
setEmployees(prev => [res, ...prev]);
}


async function handleUpdate(id, emp) {
const res = await updateEmployee(id, emp);
setEmployees(prev => prev.map(e => e._id === id ? res : e));
setEditing(null);
}


async function handleDelete(id) {
await deleteEmployee(id);
setEmployees(prev => prev.filter(e => e._id !== id));
}


return (
<div className="container">
<h1>Employee Management</h1>
<EmployeeForm onCreate={handleCreate} editing={editing} onUpdate={handleUpdate} onCancel={() => setEditing(null)} />
<EmployeeList employees={employees} onEdit={(e) => setEditing(e)} onDelete={handleDelete} />
</div>
);
}
